import React, { Component } from 'react';
import axios from 'axios';

import EditItem from '../items/EditItem.js';

class AddItem extends Component {
  constructor(props){
      super(props);
      this.state = { 
        newCategory: "", 
        newName: "",
        // newWeight: "",
      };
  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    axios.post("http://localhost:5000/api/items", {
        theCategory: this.state.newCategory,
        theName: this.state.newName,
        // theWeight: this.state.newWeight,
        theTrip: this.props.theTripToAddItemsTo
      })
    .then( () => {
        this.props.getData();
        // this function updates the list in TripIndex.js
        this.setState({
          newCategory: "", 
          newName: "", 
          // newWeight: ""
        });
    })
    .catch( error => console.log(error) )
  }

  handleChange = (event) => {  
      const {name, value} = event.target;
      this.setState({[name]: value});
  }

  render(){
    const allTheTrips = this.props.allTheTrips;
    const theID = this.props.match.params.theID;

    const theActualTrip = allTheTrips.find((eachP)=>{
        return eachP._id === theID
    })

    const showItems = () =>{
      return theActualTrip.items.map((eachItem, index)=>{
          console.log(eachItem)
          if(this.state.editing !== index)
              return ( <li key={eachItem._id}>
                          <h4>{eachItem.category}</h4>
                          <h6>{eachItem.name}</h6>
                          <button onClick = {()=>{this.edit(index)}}>Edit</button>
                          <button onClick = {()=>{this.deleteItem(eachItem._id)}}>Delete</button>
                      </li>
              )
          else
              return(
                  <EditItem
                      theItem ={eachItem}
                      getAllTheTripsInAppJS = {this.props.getData}
                      resetEditingSituation = {this.resetEdit}
                  />
              )
      })  
    }

    return(
      <div>
        <div className= "clothing">
          <h3>Clothing</h3>
          <hr />
          <div>
            {theActualTrip.items.length > 0 && 
              <ul>
                  {showItems()}
              </ul>                           
            }
          </div>
          <hr /> 
          <div className="add-item">
            <AddItem 
              theTripToAddItemsTo = {theActualTrip._id} 
              getData = {this.props.getData}
            />
          </div>
        </div>

        <form onSubmit={this.handleFormSubmit}>
        <label> Category: </label>
          <br/>
          <input type="text" name="newCategory" value={this.state.newCategory} onChange={this.handleChange}/>
          <br/>
          <label> Item Name: </label>
          <br/>
          <textarea name="newName" value={this.state.newName} onChange={this.handleChange} />
          <br/>

          {/* <label> Weight: </label>
          <textarea name="newWeight" value={this.state.newWeight} onChange={this.handleChange} />
          <br/> */}
          <input type="submit" value="Add to List" />
        </form>
      </div>
    )
  }
}


export default AddItem;
